'use client';

import { useState } from 'react';
import { useAuth } from '@/contexts/AuthContext';
import { useRouter } from 'next/navigation';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { toast } from 'sonner';
import Link from 'next/link';
import { LogIn, Loader2, Mail, Lock, Chrome } from 'lucide-react';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const { signIn, signInWithGoogle } = useAuth();
  const router = useRouter();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    const toastId = toast.loading('Signing you in...');

    try {
      await signIn(email, password);
      toast.success('Welcome back!', { 
        id: toastId,
        description: 'Successfully signed in to your account',
      });
      router.push('/dashboard');
    } catch (err: any) {
      toast.error('Sign in failed', {
        id: toastId,
        description: err.message || 'Invalid email or password',
      });
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSignIn = async () => {
    setLoading(true);
    const toastId = toast.loading('Signing in with Google...');

    try {
      await signInWithGoogle();
      toast.success('Welcome!', {
        id: toastId,
        description: 'Successfully signed in with Google',
      });
      router.push('/dashboard');
    } catch (err: any) {
      toast.error('Google sign in failed', {
        id: toastId,
        description: err.message || 'Please try again',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen p-4 bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Card className="w-full max-w-md shadow-xl border-0">
        <CardHeader className="space-y-2 pb-6 bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-t-lg">
          <div className="flex items-center justify-center mb-2">
            <div className="h-14 w-14 rounded-full bg-white/20 flex items-center justify-center">
              <LogIn className="h-7 w-7 text-white" />
            </div>
          </div>
          <CardTitle className="text-center text-2xl text-white">Login</CardTitle>
          <CardDescription className="text-center text-white/90">
            Sign in to your account to continue
          </CardDescription>
        </CardHeader>
        <CardContent className="pt-6">
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="email" className="flex items-center gap-2">
                <Mail className="h-4 w-4 text-gray-500" />
                Email
              </Label>
              <Input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="you@example.com"
                required
                disabled={loading}
                className="transition-all focus:ring-2 focus:ring-blue-200"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="password" className="flex items-center gap-2">
                <Lock className="h-4 w-4 text-gray-500" />
                Password
              </Label>
              <Input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                required
                disabled={loading}
                className="transition-all focus:ring-2 focus:ring-blue-200"
              />
            </div>
